constructor-arg.value:
%macro durian;
  %let fruitname = ${fruit.name};

  DATA ${fruit.name};
    INFILE '${fruit.data.filepath}';
    INPUT Name $ 1-15 Genus $ Family $ 
  RUN;

  PROC SORT DATA = ${fruit.name};
  BY ${fruit.sortBy};

  PROC PRINT DATA = ${fruit.name} NOOBS;
    TITLE &fruitname;
    VAR Name Genus Family;
  RUN;
%mend durian;
